package com.class5;

public class TaskAndOr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int day=3;
	
	if (day>=1 && day<6) {
		System.out.println("It is a weekday");
	}else if (day==6 && day==7) {
		System.out.println("It is a weekend");

	}else {
		System.out.println("Invalid day");
	}
		
	}
		
		
		
		

}
